import React from "react";
import { FlatList, StyleSheet, View, TouchableOpacity, Text } from "react-native"; // Import TouchableOpacity and Text
import { useTheme } from "@react-navigation/native";
import { modalDemos } from "@/components/homeData"; // Assuming modalDemos contains lesson data
import { DemoCard } from "@/components/DemoCard";
import { useRouter } from 'expo-router'; // Import useRouter

export default function HomeScreen() {
  const theme = useTheme();
  const router = useRouter(); // Initialize router

  const renderItem = ({ item }) => {
    const isPremium = item.isPremium || false; // Assuming 'isPremium' field exists in lesson data
    const isLocked = item.isLocked || false; // Assuming 'isLocked' field exists (or derive it based on user's premium status)
    const lockReason = item.lockReason || "";

    const handlePress = () => {
      console.log(`Lesson: ${item.route}, isPremium: ${isPremium}, isLocked: ${isLocked}, lockReason: ${lockReason}`); // Debugging logs

      if (isPremium) {
        router.push('/premium-coming-soon');
      } else {
        router.push(item.route); // Navigate to the lesson content
      }
    };

    return (
      <TouchableOpacity onPress={handlePress}>
        <DemoCard item={item} />
        {isPremium && <Text>Premium Lesson</Text>} {/* Add a visual indicator for premium lessons */}
      </TouchableOpacity>
    );
  };

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <FlatList
        data={modalDemos}
        renderItem={renderItem}
        keyExtractor={(item) => item.route}
        contentContainerStyle={styles.listContainer}
        contentInsetAdjustmentBehavior="automatic"
        showsVerticalScrollIndicator={false}
      />
    </View>
  );
}

const styles = StyleSheet.create({
